import React from 'react';
import { useRequest } from 'ahooks';
import {
  Button, Image, Modal, notification, Table,
} from 'antd';
import { ExclamationCircleFilled } from '@ant-design/icons';
import {
  deleteItem,
  getAllBrands, getAllCategories, getAllItems,
} from '../../service';
import './index.scss';

function ItemList() {
  const { confirm } = Modal;

  // fetchCalls
  const { data: itemList, loading: itemListLoading, run: refetchItem } = useRequest(async () => getAllItems());
  const { data: categoryList, loading: categoryListLoading } = useRequest(async () => getAllCategories());
  const { data: brandList, loading: brandListLoading } = useRequest(async () => getAllBrands());

  const generateCategoryString = (categoryId) => {
    const index = categoryList.findIndex((category) => category.id === categoryId);
    if (index === -1 || !categoryList[index]) {
      return '';
    }
    if (!categoryList[index].parentId) {
      return categoryList[index]?.name;
    }
    const parentIndex = categoryList
      .findIndex((category) => category.id === categoryList[index].parentId);
    if (parentIndex !== -1) {
      return `${categoryList[parentIndex]?.name} / ${categoryList[index]?.name}`;
    }
    return '';
  };

  const generateBrandString = (brandId) => {
    const index = brandList.findIndex((brand) => brand.id === brandId);
    if (index !== -1) {
      return brandList[index].name;
    }
    return '';
  };

  // modal:
  const showDeleteConfirm = (id) => {
    confirm({
      title: '提示',
      icon: <ExclamationCircleFilled />,
      content: '是否要进行删除操作?',
      okText: '确定',
      okType: 'danger',
      cancelText: '取消',
      async onOk() {
        const response = await deleteItem(id);
        if (response.status === 200) {
          notification.success({ message: '删除成功', description: '商品已被永久移除.' });
          await refetchItem();
        } else {
          notification.error({ message: '删除失败', description: '系统删除时遇到了问题，请联系管理员.' });
        }
      },
    });
  };

  const columns = [
    {
      title: '编号',
      dataIndex: 'id',
      key: 'id',
    },
    {
      title: '商品图片',
      dataIndex: 'image',
      key: 'image',
      render: (item) => (
        <Image
          width={200}
          height={200}
          src={item || 'data:image/png;base64,'}
          fallback="data:image/png;base64,"
        />
      ),
    },
    {
      title: '商品名称',
      dataIndex: 'name',
      key: 'name',
      render: (_, record) => (
        <div>
          <div>
            {record.name}
            {record.subtitle}
          </div>
          <div style={{ marginTop: '20px' }}>
            品牌:&nbsp;&nbsp;
            {generateBrandString(record.brand)}
          </div>
        </div>
      ),
    },
    {
      title: '分类',
      dataIndex: 'category',
      key: 'category',
      render: (item) => (
        generateCategoryString(item)
      ),
    },
    {
      title: '价格/货号',
      key: 'price',
      render: (_, record) => (
        <div>
          <div>
            价格:&nbsp;&nbsp;
            ￥
            {record.price}
          </div>
          <div style={{ marginTop: '20px' }}>
            货号:&nbsp;&nbsp;
            {record.itemNumber}
          </div>
        </div>
      ),
    },
    {
      title: '排序',
      dataIndex: 'sort',
      key: 'sort',
      render: (sort) => {
        if (!sort) {
          return 0;
        }
        return sort;
      },
    },
    {
      title: '销量',
      dataIndex: 'sold',
      key: 'sold',
      render: (sold) => {
        if (!sold) {
          return 0;
        }
        return sold;
      },
    },
    {
      title: '操作',
      key: 'control',
      render: (_, record) => (
        <div>
          <div>
            <Button shape="round">查看</Button>
            <Button shape="round" style={{ marginLeft: '16px' }}>编辑</Button>
          </div>
          <div style={{ marginTop: '16px' }}>
            <Button shape="round">日志</Button>
            <Button shape="round" type="primary" danger style={{ marginLeft: '16px' }} onClick={() => { showDeleteConfirm(record.id); }}>删除</Button>
          </div>
        </div>
      ),
    },

  ];
  return !categoryListLoading && !itemListLoading && !brandListLoading && (
    <div className="item-list">
      <Table dataSource={itemList} columns={columns} pagination={{ pageSize: 5 }} scroll={{ x: 1300 }} />
    </div>
  );
}

export default ItemList;
